  ('fr-040', 'Chateau de Mairy', 'Champagne', 'Marne', 'Mairy, Champagne, France', 'Hidden amidst the lush landscapes of Champagne, offering rustic charm combined with French elegance. Features the informal and relaxed atmosphere of a country house, perfect for a romantic, champagne-filled wedding weekend.', 20, 80, 15000, 40000, 'EUR', 'https://www.chateaudemairy.com', 'https://maps.google.com/?cid=11421612234709114598', ST_SetSRID(ST_MakePoint(4.4175767, 48.8827345), 4326)::geography, 'ChIJx0Z3oZjm60cR5p4XLNC3gZ4', 4.7, 81, 'json_migration'),
  ('fr-041', 'Chateau Mathilde', 'Normandy', 'Eure', 'Normandy, France', 'A striking chateau with red brickwork, ornate design, and space to sleep up to 45 guests. Beautifully restored and popular for smaller Normandy weddings. Combines fairy-tale architecture with comfortable modern amenities.', 30, 100, 10000, 25000, 'EUR', NULL, 'https://maps.google.com/?cid=14577497453616706863', ST_SetSRID(ST_MakePoint(0.7865626, 49.2743321), 4326)::geography, 'ChIJf4bNJ6YG4UcRLzX8_LKsTco', 4.6, 400, 'json_migration'),
